import React, { Component } from "react";
import Filterbox from "./components/Filterbox/Filterbox.component";
import AllListings from "./components/AllListings/AllListings.component";
import "./App.css";

class App extends Component {
  constructor() {
    super();

    this.state = {
      listings: [],
      activeFilters: ["Frontend", "CSS", "JavaScript"],
    };
  }

  componentDidMount() {
    fetch("./data/data.json")
      .then((response) => response.json())
      .then((listings) => this.setState({ listings: listings }));
  }

  updateFilters = (added, removed) => {
    if (added) {
      added.forEach((filter) => {
        this.setState({ activeFilters: activeFilters.push(element) });
      });
    }

    if (removed) {
      removed.forEach((filter) => {
        const filterIndex = this.state.activeFilters.findIndex(filter);
        if (filterIndex != -1) {
          const newFilters = this.state.activeFilters;
          newFilters.splice(filterIndex, 1);
          this.setState({
            activeFilters: newFilters,
          });
        }
      });
    }
  };

  render() {
    return (
      <div className='App'>
        <header className='App-header'>
          <h1>Static Job Listings Site</h1>
        </header>
        <main className='App-main'>
          {this.state.activeFilters.length > 0 ? (
            <Filterbox
              activeFilters={this.state.activeFilters}
              updateFilters={this.updateFilters}
            />
          ) : null}
          <AllListings />
        </main>
      </div>
    );
  }
}

export default App;
